import React, { useState, useEffect } from "react";
import { BrowserRouter as Router } from "react-router-dom";
import "./App.css";
import Navbar from "./components/Navbar";
import Main from "./components/Main";
import Modal from "./components/Modal";

function App() {
  const [showModal, setShowModal] = useState(false);
  const [carousel, setCarousel] = useState(1);
  const [borderTmbn, setBorderTmbn] = useState(null);
  const [cart, setCart] = useState(false);

  useEffect(() => {
    console.log("test");
  }, [carousel, borderTmbn, cart]);

  const handleClose = () => {
    setShowModal(!showModal);
    console.log(showModal);
  };

  const showCart = (e) => {
    const cartModal = document.querySelector(".cart-modal");

    setCart(!cart);
    if (e.target.tagName === "svg" || e.target.tagName === "path") {
      cartModal.classList.toggle("hidden");
      console.log(cartModal);
    } else e.target.tagName !== "svg" || e.target.tagName !== "path";

    console.log(e.target.tagName === "svg" || e.target.tagName === "path" ? "done" : "no");
  };

  function handleClick(e) {
    const cartModal = document.querySelector(".cart-modal");
    const tmbns = document.querySelectorAll(".product li");

    const tmbnail = [];

    tmbns.forEach((li) => {
      tmbnail.push(li);
    });

    if (e.target.tagName === "svg") {
      cartModal.classList.toggle("hidden");
    } else if (e.target.tagName === "path") {
      cartModal.classList.toggle("hidden");
    }
    // else {
    //   if (!e.target.tagName.parentNode.classList.contains("cart-modal")) {
    //     cartModal.classList.add("hidden");
    //   } else {
    //   }
    // }
  }

  return (
    <Router>
      <div onClick={showCart} className="App lg:w-3/4 md:w-4/5 mx-auto font-body">
        <Navbar handleClick={handleClick} />
        <Main setShowModal={setShowModal} setBorderTmbn={setBorderTmbn} setCarousel={setCarousel} />
        {showModal && <Modal handleClose={handleClose} setCarousel={setCarousel} carousel={carousel} setBorderTmbn={setBorderTmbn} borderTmbn={borderTmbn} />}
      </div>
    </Router>
  );
}

export default App;
