    const list = document.querySelectorAll(".product ul li");

    [...list].map((li, i) => {
      if (e.target == li.firstElementChild) {
        setShowModal(true);
        setCarousel(i + 1);
        setBorderTmbn(i + 1);
      }
    });
  };

  return (
    <div className="product box-border md:px-28 md:pt-14 md:pb-2 ">
      <img src={imgProduct01} alt="image-product" className=" rounded-2xl mb-6 mx-auto" />
