import React from "react";
import imgProduct01 from "../images/image-product-1.jpg";
import imgProduct02 from "../images/image-product-2.jpg";
import imgProduct03 from "../images/image-product-3.jpg";
import imgProduct04 from "../images/image-product-4.jpg";
import thmb01 from "../images/image-product-1-thumbnail.jpg";
import thmb02 from "../images/image-product-2-thumbnail.jpg";
import thmb03 from "../images/image-product-3-thumbnail.jpg";
import thmb04 from "../images/image-product-4-thumbnail.jpg";

const Modal = ({ handleClose, setCarousel, carousel }) => {
  const imgs = [imgProduct01, imgProduct02, imgProduct03, imgProduct04];

  const nextSelect = () => {
    if (carousel >= 4) {
      setCarousel(1);
    } else {
      setCarousel(carousel + 1);
    }
  };

  const prevSelect = () => {
    if (carousel <= 1) {
      setCarousel(4);
    } else {
      setCarousel(carousel - 1);
    }
  };

  // console.log(carousel);

  return (
    <div className="modal flex items-center justify-center absolute inset-0 bg-opacity ">
      <div className="lg:w-3/12 lg:h-3/12 md:w-5/12 md:h-5/12 w-3/6 h-3/6 relative">
        <button onClick={prevSelect} className="absolute group top-1/3 -left-4 bg-white-2 w-10 h-10 flex items-center justify-center rounded-full">
          <svg width="12" height="18" xmlns="http://www.w3.org/2000/svg" className="stroke-black group-hover:stroke-primary-1">
            <path d="M11 1 3 9l8 8" strokeWidth="3" fill="none" fillRule="evenodd" />
          </svg>
        </button>

        {/* {imgs.map((img, i) => (
          {carousel && i && <img src={img} alt="product-image" className="rounded-2xl mb-6 " />}
          
        ))} */}
        {imgs.filter((img, i) => i === carousel)}

        {/* <img src={imgProduct01} alt="product-image" className="rounded-2xl mb-6 hidden" />
        <img src={imgProduct02} alt="product-image" className="rounded-2xl mb-6 hidden" />
        <img src={imgProduct03} alt="product-image" className="rounded-2xl mb-6 " />
        <img src={imgProduct04} alt="product-image" className="rounded-2xl mb-6 hidden" /> */}
        <button onClick={nextSelect} className=" absolute group top-1/3 -right-4 bg-white-2 w-10 h-10 flex items-center justify-center rounded-full ">
          <svg width="13" height="18" xmlns="http://www.w3.org/2000/svg" className="stroke-black group-hover:stroke-primary-1">
            <path d="m2 1 8 8-8 8" strokeWidth="3" fill="none" fillRule="evenodd" />
          </svg>
        </button>

        <ul className="thumbnails flex justify-between w-4/5 mx-auto ">
          <li className="group outline rounded-lg overflow-hidden outline-transparent active:outline-primary-1 outline-2 w-1/5">
            <img src={thmb01} alt="thumbnail-1 " className="tmbn rounded-lg group-hover:opacity-30" />
          </li>
          <li className="group outline rounded-lg overflow-hidden outline-transparent active:outline-primary-1 outline-2 w-1/5">
            <img src={thmb02} alt="thumbnail-2 " className="tmbn rounded-lg group-hover:opacity-30" />
          </li>
          <li className="group outline rounded-lg overflow-hidden outline-transparent active:outline-primary-1 outline-2 w-1/5">
            <img src={thmb03} alt="thumbnail-3 " className="tmbn rounded-lg group-hover:opacity-30" />
          </li>
          <li className="group outline rounded-lg overflow-hidden outline-transparent active:outline-primary-1 outline-2 w-1/5">
            <img src={thmb04} alt="thumbnail-4 " className="tmbn rounded-lg group-hover:opacity-30" />
          </li>
        </ul>
        <p>{carousel}</p>
        <button onClick={handleClose} className="close group absolute right-0 -top-5">
          <svg width="14" height="15" xmlns="http://www.w3.org/2000/svg" className="fill-black group-hover:fill-primary-1">
            <path d="m11.596.782 2.122 2.122L9.12 7.499l4.597 4.597-2.122 2.122L7 9.62l-4.595 4.597-2.122-2.122L4.878 7.5.282 2.904 2.404.782l4.595 4.596L11.596.782Z" fill-rule="evenodd" />
          </svg>
        </button>
      </div>
    </div>
  );
};

export default Modal;
