import React, { useState, useEffect } from "react";
import { BrowserRouter as Router } from "react-router-dom";
import "./App.css";
import Navbar from "./components/Navbar";
import Main from "./components/Main";
import Modal from "./components/Modal";

function App() {
  const [showModal, setShowModal] = useState(false);
  const [carousel, setCarousel] = useState(1);
  const [borderTmbn, setBorderTmbn] = useState(null);

  useEffect(() => {
    console.log("test");
  }, [carousel, borderTmbn]);

  const handleClose = () => {
    setShowModal(!showModal);
    console.log(showModal);
  };

  function handleClick(e) {
    const cartModal = document.querySelector(".cart-modal");
    const tmbns = document.querySelectorAll(".product li");

    const tmbnail = [];

    tmbns.forEach((li) => {
      tmbnail.push(li);
    });

    if (e.target.tagName === "svg" || e.target.tagName === "path") {
      cartModal.classList.toggle("hidden");
    } else {
      cartModal.classList.add("hidden");
    }
  }

  return (
    <Router>
      <div className="App  lg:w-3/4 md:w-4/5 mx-auto font-body">
        <Navbar handleClick={handleClick} />
        <Main handleClick={handleClick} />
        {showModal && <Modal handleClose={handleClose} setCarousel={setCarousel} carousel={carousel} setBorderTmbn={setBorderTmbn} borderTmbn={borderTmbn} />}
      </div>
    </Router>
  );
}

export default App;
